",".preload {
  position: fixed;
  top: 0;
  width: 100%;
  height: 100vh;
  background: black;
  display: flex;
  justify-content: center;
  align-items: center;
  transition: all 0.5s ease;
  flex-direction: column;
  z-index: 99;

  .preload_img {
    animation-name: spin;
    animation-duration: 2000ms;
    animation-iteration-count: infinite;
    animation-timing-function: linear;
    margin-bottom: 5rem;
    width: 20%;
  }

  .preload_text {
    color: white;
    font-size: 2rem;
  }
}

.preload-finish {
  opacity: 0;
  pointer-events: none;
}

@keyframes spin {
  from {
    transform: rotate(0deg);
  }

  to {
    transform: rotate(360deg);
  }
}
